import React,{useState} from "react";
export default function Textform(props) {
  const handleclick =()=>{
    console.log(text)
    let newText = text.toUpperCase();
    setText(newText)
  }
  const handleonchange =(event)=>{
    setText(event.target.value)
  }
  const handlelower =()=>{
    console.log(text)
    let newText = text.toLowerCase();
    setText(newText)
  }
  const handleclear=() =>{
    let newText= ''
    setText(newText)
  }

  const handlereplace=()=>{
    let arr1=prompt("Enter the word to be replaced:")
    let arr2= prompt("Enter new word")
    setText(text.replace(arr1,arr2))
  }
  const[arr1,arr2]=useState("");
  const[text,setText]= useState("");
  //text="akmsklkas"-wrong way
  //setText("hellloooo");- correct way
  return (
    <>
    <div className="container">
      <h1>{props.heading}</h1>
      <div className="mb-3">
        <textarea className="form-control" value={text} onChange={handleonchange} id="myBox" rows="8"></textarea>
        <button className="btn btn-outline-primary mx-2 mt-2" onClick={handleclick}>Convert to uppercase</button>
        <button className="btn btn-outline-primary mx-2 mt-2" onClick={handlelower}>Convert to lowercase</button>
        <button className="btn btn-outline-primary mx-2 mt-2" onClick={handleclear}>Clear Text</button>
        <button className="btn btn-outline-primary mx-2 mt-2" onClick={handlereplace}>Replace words</button>
      </div>
    </div>
    <div className="container my-3">
        <h1>Your text summary</h1>
        <p>{text.split(' ').length} words and {text.length} characters</p>
        <p>{0.008*text.split(' ').length} minutes read</p>
    </div>
  </>
  );
}
